import axios from "axios";
import Web3 from "web3";

//implemented chainLinks price feeds but didn't work well at end

const web3 = new Web3();
const COINGECKO_API_URL1 ="https://min-api.cryptocompare.com/data/price?fsym=ETH&tsyms=USD";
const COINGECKO_API_URL2 ="https://min-api.cryptocompare.com/data/price?fsym=MATIC&tsyms=USD";
const COINGECKO_API_URL3 ="https://min-api.cryptocompare.com/data/price?fsym=LINK&tsyms=USD";
const COINGECKO_API_URL4 ="https://min-api.cryptocompare.com/data/price?fsym=AAVE&tsyms=USD";
const COINGECKO_API_URL5 ="https://min-api.cryptocompare.com/data/price?fsym=UNI&tsyms=USD";

export let usdPrice = 0;

export const getEthPriceInUSD = async () => {
  try {
    const response = await axios.get(COINGECKO_API_URL, {
      params: {
        ids: "ethereum",
        vs_currencies: "usd",
      },
    });



    const ethPriceInUSD = response.data.USD;
    usdPrice = ethPriceInUSD;
    console.log("getEth price in usd : ", usdPrice);
    return ethPriceInUSD;
  } catch (error) {
    console.error("Error fetching Ethereum price:", error);
    return null;
  }
};

export const convertWeiToUSD = (wei) => {
  const etherValue = web3.utils.fromWei(wei, "ether");
  return etherValue;
};
