        console.log(prices);
        setETH(prices.ethPriceInUSD);
        setMATIC(prices.ethPriceInUSD2);
        setLINK(prices.ethPriceInUSD3);
        setAAVE(prices.ethPriceInUSD4);
        setUNI(prices.ethPriceInUSD5);
        console.log( {ETH} );


