import React, { useState } from 'react';
import './Body.css';

import { ethers } from 'ethers';

const Body = () => {
  return (
    <div className="container">
      <div className="shapes">
        {/* Minimalistic Shapes */}
        <svg className="circle" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100">
          <circle cx="50" cy="50" r="40" />
        </svg>

        <svg className="triangle" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100">
          <polygon points="50,15 90,85 10,85" />
        </svg>

        <svg className="line" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 10">
          <rect width="100" height="10" />
        </svg>
      </div>

      <h1 className="heading">Welcome to My Minimalistic Page</h1>
      <button className="styled-button" onClick={ConnectWallet}>Click Me</button>
    </div>
  );
};

const ConnectWallet = () => {
    const [account, setAccount] = useState(null);

    const connectWallet = async () => {
      if (window.ethereum) {
        try {
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const accounts = await provider.send("eth_requestAccounts", []);
          setAccount(accounts[0]);
        } catch (error) {
          console.error("Connection to MetaMask failed:", error);
        }
      } else {
        alert('MetaMask is not installed. Please install it to use this feature.');
      }
    };
} 
export default Body;
