import React, { useState } from 'react';
import './ConnectWallet.css';

const ConnectWallet = () => {
  const [account, setAccount] = useState(null);
  const [balance, setBalance] = useState(null);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        // Request account access
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);

        // Specify Sepolia network and fetch balance
        const balanceInWei = await fetchBalanceFromSepolia(accounts[0]);
        const balanceInEther = weiToEther(balanceInWei);
        setBalance(balanceInEther);
      } catch (error) {
        console.error("User rejected the request or an error occurred:", error);
      }
    } else {
      alert('MetaMask is not installed. Please install it to use this feature.');
    }
  };

  const fetchBalanceFromSepolia = async (address) => {
    const response = await fetch('https://sepolia.infura.io/v3/YOUR_INFURA_PROJECT_ID', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        jsonrpc: '2.0',
        method: 'eth_getBalance',
        params: [address, 'latest'],
        id: 1,
      }),
    });

    const data = await response.json();
    return data.result;
  };

  const weiToEther = (wei) => {
    // Convert wei to ether
    return parseInt(wei, 16) / 1e18;
  };

  return (
    <div className="wallet-container">
      <button className="connect-button" onClick={connectWallet}>
        {account ? `Connected: ${account}` : "Connect MetaMask"}
      </button>
      {account && (
        <div className="wallet-info">
          <p>Balance: {balance ? `${balance} ETH` : 'Fetching balance...'}</p>
        </div>
      )}
    </div>
  );
};

export default ConnectWallet;
