import React, { useState } from 'react';
import './ConnectWallet.css';

const ConnectWallet = () => {
  const [account, setAccount] = useState(null);
  const [balance, setBalance] = useState(null);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        // Request account access if needed
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        setAccount(accounts[0]);

        // Fetch the balance
        const provider = new window.ethers.providers.Web3Provider(window.ethereum);
        const balanceInWei = await provider.getBalance(accounts[0]);
        const balanceInEther = window.ethers.utils.formatEther(balanceInWei);
        setBalance(balanceInEther);
      } catch (error) {
        console.error("User rejected the request or an error occurred:", error);
      }
    } else {
      alert('MetaMask is not installed. Please install it to use this feature.');
    }
  };

  return (
    <div className="wallet-container">
      <button className="connect-button" onClick={connectWallet}>
        {account ? `Connected: ${account}` : "Connect MetaMask"}
      </button>
      {account && (
        <div className="wallet-info">
          <p>Balance: {balance ? `${balance} ETH` : 'Fetching balance...'}</p>
        </div>
      )}
    </div>
  );
};

export default ConnectWallet;
